import React, { useState } from "react";
import "home.cs";

// Popup component to show detailed card info
const Popup = ({ content, onClose }) => {
  return (
    <div className="popup">
      <div className="popup-content">
        <h2>{content.title}</h2>
        <p>{content.description}</p>
        <button className="reed-more-btn" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
};

const ServicesSection = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);

  // Card data to display in popup
  const cards = [
    {
      id: 1,
      title: "Animation",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cum, error eius. Delectus excepturi sed nostrum eos nobis cupiditate eius temporibus reprehenderit exercitationem ab fugit sapiente tempore labore, repellendus expedita ex!",
      img: "./images/animate.png",
    },
    {
      id: 2,
      title: "Character Creator",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cum, error eius. Delectus excepturi sed nostrum eos nobis cupiditate eius temporibus reprehenderit exercitationem ab fugit sapiente tempore labore, repellendus expedita ex!",
      img: "./images/charactor-creator.png",
    },
    {
      id: 3,
      title: "Video Editing",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cum, error eius. Delectus excepturi sed nostrum eos nobis cupiditate eius temporibus reprehenderit exercitationem ab fugit sapiente tempore labore, repellendus expedita ex!",
      img: "./images/video-editing.png",
    },
  ];

  // Function to handle opening the popup
  const openPopup = (card) => {
    setSelectedCard(card);
    setIsPopupOpen(true);
  };

  // Function to handle closing the popup
  const closePopup = () => {
    setIsPopupOpen(false);
    setSelectedCard(null);
  };

  return (
    <section className="servicesSection" id="services">
      <h1>Our Services</h1>
      <p className="subTitle">Tailored Solutions for Every Need</p>

      <div className="Card-section">
        {cards.map((card) => (
          <div className="card" key={card.id}>
            <div className="card-img">
              <img src={card.img} alt={card.title} />
            </div>
            <div className="card-content">
              <div className="cardTxt">
                <h2>{card.title}</h2>
                <p>{card.description}</p>
              </div>
              <button className="reed-more-btn" onClick={() => openPopup(card)}>
                Read More
              </button>
            </div>
          </div>
        ))}

        <div className="extra-card">
          <h2>Do You Want Something </h2>
          <h2>Different?</h2>
          <p>Let us know</p>
          <a href="#contact">
            <button className="reed-more-btn">
              Know us <i className="ri-mail-line"></i>
            </button>
          </a>
        </div>
      </div>

      {/* Render Popup if a card is selected */}
      {isPopupOpen && selectedCard && (
        <Popup content={selectedCard} onClose={closePopup} />
      )}
    </section>
  );
};

export default ServicesSection;
