import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Testimonial.css"; // Add your custom styles

const testimonialData = [
  {
    id: 1,
    text: "Very happy with this 3D Videol Exactly what I was looking for. Seller providing very cool work.",
    name: "Ubus",
    title: "Founder of Urban Bagz",
    image: "./images/image11.png",
  },
  {
    id: 2,
    text: "I just like the work, the communication and the kindness. Thank you",
    name: "Giddy",
    title: "KuhCool Kids YouTube Channel",
    image: "./images/image22.png",
  },
  {
    id: 3,
    text: "Snapnart is AMAZING. He doesn't just know his stuff, he is also very passionate and professional about what he does. He is just BRILLIANTI I highly recommend him if you want some serious animation done. 100%!!!",
    name: "Kodi Blackman",
    title: "Auther",
    image: "./images/image33.png",
  },
  {
    id: 4,
    text: "Very happy with this 3D Video! Exactly what I was looking for. Seller providing very cool work.",
    name: "xyken88",
    title: "Founder of Snug Fit",
    image: "./images/image44.png",
  },
  {
    id: 5,
    text: "It was very nice working with you. Initially I was doubtful with the delivery. But you have handled very professionally each of my request. There were more than expected revisions but I am happy with it. Will work with you again. Thanks.",
    name: "komkhat",
    title: " YouTube Channel",
    image: "./images/image55.png",
  },
  {
    id: 6,
    text: "Best Seller In 3d Animation for kids. Animox Studio's Is the Perfect Animator For kids Animation. Best Quality of Work, Quick Responsive, The Quality of Their work. I am Satisfied with their work.",
    name: "kunax studio",
    title: " YouTube Channel",
    image: "./images/image66.png",
  },
  {
    id: 7,
    text: "Best gig for 3d kids animation. I got beyond expectations. Highly recommend for kids 3d animation.",
    name: "dianelennon",
    title: " YouTube Channel",
    image: "./images/image77.png",
  },
  // Add more testimonials as needed
];

const CustomSlider = () => {
  const settings = {
    infinite: true,
    centerMode: true,
      centerPadding: "0",
    slidesToScroll: 1,
   
    slidesToShow: 3,
      speed: 500,
    autoplay: true,
    autoplaySpeed: 4000,
    pauseOnHover: true,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          centerMode: false,
        },
      },
    ],
  };

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {testimonialData.map((testimonial) => (
          <div key={testimonial.id} className="slider-item">
            <div className="testimonial-card">
              <div className="review-section">
                <p>{testimonial.text}</p>
              </div>
              <div className="author-info">
                <img src={testimonial.image} alt={testimonial.name} />
                <div>
                  <h4>{testimonial.name}</h4>
                  <p>{testimonial.title}</p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default CustomSlider;
